class article:
    def __init__(self, path=None):
        self.path = path
    
    def chunks(self):
        output = []
        if self.path:
            with open(self.path, 'r', encoding='utf-8') as file:
                lines = [line.replace("\n", "") for line in file.readlines() if line.strip()]
                for i in range(len(lines)):
                    if i == 0:
                        past = None
                        line = None
                        next_line = lines[i]
                    elif i == len(lines)-1:
                        past = lines[i-1]
                        line = lines[i]
                        next_line = None
                    else:
                        past = lines[i-1]
                        line = lines[i]
                        next_line = lines[i+1]
                    output.append([past,line,next_line])
        return output

